				["init"] = {
					["custom"] = "-- update 24/12 -- by Buds - https://wago.io/BFADungeonTargetedSpells\n\n-- Spell list\naura_env.spells = { \n    -- [spellId] = important (true or false),\n    [259832] = true,\n    \n    -- Atal'Dazar\n    [253239] = false, -- Dazar'ai Juggernaut - Merciless Assault\n    [256846] = false, -- Dinomancer Kish'o - Deadeye Aim\n    [257407] = true, -- Rezan - Pursuit\n    \n    -- Freehold\n    [272402] = true, -- Cutwater Knife Juggler - Ricocheting Throw\n    [257739] = true, -- Blacktooth Scrapper - Blind Rage\n    [258338] = true, -- Captain Raoul - Blackout Barrel\n    [256979] = true, -- Captain Eudora - Powder Shot\n    \n    -- Kings'Rest\n    [270507] = true, --  Spectral Beastmaster - Poison Barrage\n    [265773] = true, -- The Golden Serpent - Spit Gold\n    [270506] = true, -- Spectral Beastmaster - Deadeye Shot\n    \n    -- Shrine of the Storm\n    [264166] = true, -- Aqu'sirr - Undertow\n    [268214] = true, -- Runecarver Sorn - Carve Flesh\n    \n    -- Siege of Boralus\n    [257641] = false, -- Kul Tiran Marksman - Molten Slug\n    [257036] = true, -- Dockhound Packmaster - Feral Charge\n    [272662] = false, -- Irontide Raider - Iron Hook\n    [256639] = false, -- Blacktar Bomber - Fire Bomb\n    [272874] = true, -- Ashvane Commander - Trample\n    [272581] = false, -- Bilge Rat Tempest - Water Spray\n    [272528] = false, -- Ashvane Sniper - Shoot\n    [272542] = true, -- Ashvane Sniper - Ricochet\n    \n    -- Temple of Sethraliss\n    [268703] = false, -- Charged Dust Devil - Lightning Bolt\n    [272670] = false, -- Sandswept Marksman - Shoot\n    [267278] = false, -- Static-charged Dervish - Electrocute\n    [272820] = false, -- Spark Channeler - Shock\n    [274642] = false, -- Hoodoo Hexer - Lava Burst\n    [268061] = false, -- Plague Doctor - Chain Lightning\n    \n    -- The Motherlode!!\n    [258674] = false, -- Off-Duty Laborer - Throw Wrench\n    [276304] = false, -- Rowdy Reveler - Penny For Your Thoughts\n    [263628] = true, -- Mechanized Peacekeeper - Charged Claw\n    [263209] = false, -- Mine Rat - Throw Rock\n    [263202] = false, -- Venture Co. Earthshaper - Rock Lance\n    [262794] = false, -- Venture Co. Mastermind - Energy Lash\n    [260669] = true, -- Rixxa Fluxflame - Propellant Blast\n    \n    -- The Underrot\n    [265376] = true, -- Fanatical Headhunter - Barbed Spear\n    [265084] = false, -- Devout Blood Priest - Blood Bolt\n    [265625] = true, -- Befouled Spirit - Dark Omen\n    \n    -- Tol Dagor\n    [256039] = true, -- Overseer Korgus - Deadeye\n    [185857] = false, -- Ashvane Spotter - Shoot\n    \n    -- Waycrest Manor\n    [263891] = false, -- Heartsbane Vinetwister - Grasping Thorns\n    [264510] = false, -- Crazed Marksman - Shoot\n    [260699] = false, -- Coven Diviner - Soul Bolt\n    [260551] = true, -- Soulbound Goliath - Soul Thorns\n    [260741] = true, -- Heartsbane Triad - Jagged Nettles\n    [268202] = true, -- Gorak Tul - Death Lens\n}\n\naura_env.blacklist_npc = {\n    [120651] = true, -- explosive orb affix\n}\naura_env.blacklist_spell = {\n    \n}\n\nlocal versionTarget = \"2.9.1-1-ga2b83c3\"\n\nlocal function needUpdate(actual, target)\n    local function splitByDot(str)\n        str = str or \"\"\n        local t, count = {}, 0\n        str:gsub(\"([^%.%-]+)\", function(c)\n                count = count + 1\n                t[count] = c\n        end)\n        return t\n    end\n    \n    local sActual = splitByDot(actual)\n    local sTarget = splitByDot(target)\n    \n    local c = 1\n    local newer = false\n    while sActual[c] and sActual[c] do\n        if sActual[c] > sTarget[c] then\n            newer = true\n            break\n        end\n        c = c + 1\n    end\n    return not newer\nend\n\nif needUpdate(WeakAuras.versionString, versionTarget) then\n    print(aura_env.id .. \"require WeakAuras version >= \".. versionTarget)\nend\n-- DO NOT EDIT BELOW --\n\naura_env.position = {\"LEFT\",\"TOPLEFT\",\"TOP\",\"TOPRIGHT\",\"RIGHT\",\"BOTTOMRIGHT\",\"BOTTOM\",\"BOTTOMLEFT\",\"CENTER\"}\naura_env.direction = {\"LEFT\",\"RIGHT\",\"TOP\",\"BOTTOM\",\"HORIZONTAL\",\"VERTICAL\"}\n\naura_env.isBlackListed = function(spellId, unit)\n    local guid = UnitGUID(unit)\n    local npc_id= select(6,strsplit(\"-\",guid))\n    return aura_env.blacklist_spell[spellId] or aura_env.blacklist_npc[npc_id]\nend\n\naura_env.allcasts = {}\n\n-- GetFrame\n\nlocal frame_priority = {\n    -- raid frames\n    [1] = \"^Vd1\", -- vuhdo\n    [2] = \"^Healbot\", -- healbot\n    [3] = \"^GridLayout\", -- grid\n    [4] = \"^Grid2Layout\", -- grid2\n    [5] = \"^ElvUF_RaidGroup\", -- elv\n    [6] = \"^oUF_bdGrid\", -- bdgrid\n    [7] = \"^oUF.*raid\", -- generic oUF\n    [8] = \"^LimeGroup\", -- lime\n    [9] = \"^SUFHeaderraid\", -- suf\n    [10] = \"^CompactRaid\", -- blizz\n    -- party frames\n    [11] = \"^SUFHeaderparty\", --suf\n    [12] = \"^ElvUF_PartyGroup\", -- elv\n    [13] = \"^oUF.*party\", -- generic oUF\n    [14] = \"^PitBull4_Groups_Party\", -- pitbull4\n    [15] = \"^CompactParty\", -- blizz\n    -- player frame\n    [16] = \"^SUFUnitplayer\",\n    [17] = \"^PitBull4_Frames_Player\",\n    [18] = \"^ElvUF_Player\",\n    [19] = \"^oUF.*player\",\n    [20] = \"^PlayerFrame\",\n}\n\nWA_GetFramesCache = WA_GetFramesCache or {}\nif not WA_GetFramesCacheListener then\n    WA_GetFramesCacheListener = CreateFrame(\"Frame\")\n    local f = WA_GetFramesCacheListener\n    f:RegisterEvent(\"PLAYER_REGEN_DISABLED\")\n    f:RegisterEvent(\"PLAYER_REGEN_ENABLED\")\n    f:RegisterEvent(\"GROUP_ROSTER_UPDATE\")\n    f:SetScript(\"OnEvent\", function(self, event, ...)\n            WA_GetFramesCache = {}\n    end)\nend\n\nlocal function GetFrames(target)\n    local function FindButtonsForUnit(frame, target)\n        local results = {}\n        if type(frame) == \"table\" and not frame:IsForbidden() then\n            local type = frame:GetObjectType()\n            if type == \"Frame\" or type == \"Button\" then\n                for _,child in ipairs({frame:GetChildren()}) do\n                    for _,v in pairs(FindButtonsForUnit(child, target)) do\n                        tinsert(results, v)\n                    end\n                end\n            end\n            if type == \"Button\" then\n                local unit = frame:GetAttribute('unit')\n                if unit and frame:IsVisible() and frame:GetName() then\n                    WA_GetFramesCache[frame] = unit\n                    if UnitIsUnit(unit, target) then\n                        -- print(\"F:\", frame:GetName())\n                        tinsert(results, frame)\n                    end\n                end\n            end\n        end\n        return results\n    end\n    \n    if not UnitExists(target) then\n        if type(target) == \"string\" and target:find(\"Player\") then\n            target = select(6,GetPlayerInfoByGUID(target))\n        else\n            target = target:gsub(\" .*\", \"\")\n            if not UnitExists(target) then\n                return {}\n            end\n        end\n    end \n    \n    local results = {}\n    for frame, unit in pairs(WA_GetFramesCache) do\n        --print(\"from cache:\", frame:GetName())\n        if UnitIsUnit(unit, target) then\n            if frame:GetAttribute('unit') == unit then\n                tinsert(results, frame)\n            else\n                results = {}\n                break\n            end\n        end\n    end\n    \n    return #results > 0 and results or FindButtonsForUnit(UIParent, target)\nend\n\nlocal isElvUI = IsAddOnLoaded(\"ElvUI\")\nlocal function WhyElvWhy(frame)\n    if isElvUI and frame and frame:GetName():find(\"^ElvUF_\") and frame.Health then\n        return frame.Health\n    else\n        return frame\n    end\nend\n\n\nfunction aura_env.GetFrame(target)\n    local frames = GetFrames(target)\n    if not frames then return nil end\n    for i=1,#frame_priority do\n        for _,frame in pairs(frames) do\n            if (frame:GetName()):find(frame_priority[i]) then\n                return WhyElvWhy(frame)\n            end\n        end\n    end\n    return WhyElvWhy(frames[1])\nend\n\n\n-- Custom Glow\n\nlocal LCG = LibStub(\"LibCustomGlow-1.0\")\nlocal glowTypes = {\"Standard\",\"Pixel\",\"AutoCast\"}\nlocal glowType = glowTypes[aura_env.config.glowType]\n\naura_env.Glow = function(frame,show)\n    if show then\n        if glowType == \"AutoCast\" then\n            LCG.AutoCastGlow_Start(\n                frame,\n                aura_env.config.glowColor,\n                aura_env.config.glowParticules,\n                aura_env.config.glowFrequency,\n                aura_env.config.glowScale,\n                aura_env.config.glowxOffset,\n                aura_env.config.glowyOffset\n            )\n        elseif glowType == \"Pixel\" then\n            LCG.PixelGlow_Start(\n                frame,\n                aura_env.config.glowColor,\n                aura_env.config.glowParticules,\n                aura_env.config.glowFrequency,\n                aura_env.config.glowLength,\n                aura_env.config.glowThickness,\n                aura_env.config.glowxOffset,\n                aura_env.config.glowyOffset,\n                aura_env.config.glowBorder\n            )\n        elseif glowType == \"Standard\" then\n            LCG.ButtonGlow_Start(frame)\n        end\n    else\n        if glowType == \"AutoCast\" then\n            LCG.AutoCastGlow_Stop(frame)\n        elseif glowType == \"Pixel\" then\n            LCG.PixelGlow_Stop(frame)    \n        elseif glowType == \"Standard\" then\n            LCG.ButtonGlow_Stop(frame)\n        end\n    end\nend\n\n\n-- Nameplates\n\naura_env.addTextureToNameplate = function(unit, target)\n    local nameplate = C_NamePlate.GetNamePlateForUnit(unit)\n    if nameplate then\n        local frame = CreateFrame(\"Frame\", nil, UIParent)\n        frame:SetSize(36, 36)\n        frame:SetPoint(\"CENTER\", nameplate, \"CENTER\", aura_env.config.nameplatexOffset, aura_env.config.nameplateyOffset)\n        frame.string = frame:CreateFontString(nil, \"OVERLAY\")\n        frame.string:SetFont(aura_env.region.text2:GetFont() or \"Fonts\\\\2002.TTF\", 20, \"OUTLINE\")\n        frame.string:SetPoint(\"CENTER\", frame, \"CENTER\", 0, 0)\n        frame.string:SetText(WA_ClassColorName(target))\n        frame:Show()\n        return frame\n    end\n    return\nend",
					["do_custom"] = true,
